import type { ComponentInterface } from '@stencil/core';
import {
  Component,
  h,
  Prop,
} from '@stencil/core';

@Component({ tag: 'd-badge' })
export class DBadge implements ComponentInterface {
  /**
   * The theme to use.
   */
  @Prop() theme = 'primary';

  /**
   * The text of badge
   */
  @Prop() text?: string;

  /**
   *  Enable dot mode
   */
  @Prop() isDot = false;

  // eslint-disable-next-line @stencil/own-methods-must-be-private
  generateClasses() {
    return {
      badge: true,
      'badge-dot': this.isDot,
      [`badge-${this.theme}`]: !!this.theme,
    };
  }

  render() {
    return (
      <span class={this.generateClasses()}>
        <span>{this.text}</span>
      </span>
    );
  }
}
