/* eslint-disable @typescript-eslint/no-explicit-any */
export default {
  library: {},
  engine: undefined as any,
  init(library: any, Liquid: any) {
    this.library = library;

    if (Liquid) {
      // eslint-disable-next-line @typescript-eslint/no-var-requires,global-require
      this.engine = new Liquid.Liquid({
        strictFilters: true,
        strictVariables: true,
      });
    }
  },
  /**
   * Parse a liquid string
   * @param liquidString Target Content Space UID
   * @returns a usable object or string
   */
  async parseLiquidAsync(liquidString: string): Promise<string> {
    try {
      return this.engine.parseAndRender(liquidString, this.library);
    } catch (error: any) {
      return error.message;
    }
  },
  /**
   * Parse a liquid string
   * @param liquidString Target Content Space UID
   * @returns a usable object or string
   */
  parseLiquid(liquidString: string): string {
    try {
      return this.engine.parseAndRenderSync(liquidString, this.library);
    } catch (error: any) {
      return error.message;
    }
  },
  parse(liquidString: string): string {
    if (this.engine) {
      return this.parseLiquid(liquidString);
    }
    return liquidString;
  },
  async parseAsync(liquidString: string): Promise<string> {
    if (this.engine) {
      return this.parseLiquidAsync(liquidString);
    }
    return liquidString;
  },
};
import currency from 'currency.js';
import type { Options } from 'currency.js';

export default function formatCurrency(
  amount: number,
  options: Options,
): string {
  return currency(amount, options).format();
}
import {
  AxiosInstance,
  AxiosRequestConfig,
} from 'axios';

// eslint-disable-next-line @typescript-eslint/no-explicit-any
export type ControlledRequest<ResponseData, RequestData = any> = {
  // eslint-disable-next-line @typescript-eslint/no-explicit-any,max-len
  perform: (config?: { data?: RequestData; params?: any }) => Promise<ResponseData>;
  abort: () => void;
};

/**
 * @deprecated
 * @param client
 * @param config
 */
export default function createControlledRequest<T, R = T>(
  client: AxiosInstance,
  config: AxiosRequestConfig<T>,
): ControlledRequest<R, T> {
  const abortController = new AbortController();
  return {
    perform: async () => {
      const { data } = await client.request<R>({
        ...config,
        signal: abortController.signal,
      });
      return data;
    },
    abort: () => abortController.abort(),
  };
}
export default class Deferred<T> {
  resolve!: ((value: T) => void);

  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  reject!: ((reason: any) => void);

  promise: Promise<T>;

  constructor() {
    this.promise = new Promise((resolve, reject) => {
      this.resolve = resolve;
      this.reject = reject;
    });
  }
}
import Deferred from './Deferred';

/**
 * @deprecated
 * @param config
 * @param data
 */
export default function createMockedRequest<T, R = T>(
  data: R,
) {
  const deferred = new Deferred<void>();
  const timer = setTimeout(() => deferred.resolve(), 1000);
  return {
    abort: () => {
      deferred.reject(new Error('AbortError'));
      clearTimeout(timer);
    },
    perform: async (): Promise<R> => {
      await deferred.promise;
      return data as unknown as R;
    },
  };
}
